"\"\"\"Unit tests for __init__.py.\"\"\"\n\n\nimport pytest\nfrom unittest.mock import patch\nimport importlib.metadata\nimport sys\n\n\n\ndef test_logger_names():\n    \"\"\"Test logger names.\"\"\"\n    with patch(\"importlib.metadata.version\"):\n        import autogen_agentchat  # Patch prevents error\n    assert autogen_agentchat.TRACE_LOGGER_NAME == \"autogen_agentchat\"\n    assert autogen_agentchat.EVENT_LOGGER_NAME == \"autogen_agentchat.events\"\n\n\n\ndef test_version():\n    \"\"\"Test version retrieval.\"\"\"\n    with patch(\"importlib.metadata.version\") as mock_version:\n        mock_version.return_value = \"0.7.5\"\n        import autogen_agentchat\n        assert autogen_agentchat.__version__ == \"0.7.5\"\n        assert isinstance(autogen_agentchat.__version__, str)\n\n\n\ndef test_version_raises_without_package():\n    \"\"\"Test __version__ raises PackageNotFoundError without installed package.\"\"\"\n    # Clear any cached module\n    autogen_agentchat_mod = sys.modules.pop(\"autogen_agentchat\", None)\n    with pytest.raises(importlib.metadata.PackageNotFoundError):\n        import autogen_agentchat\n    if autogen_agentchat_mod:\n        sys.modules[\"autogen_agentchat\"] = autogen_agentchat_mod\n"